<template>
	<el-dialog 
		v-model="dialogVisible" 
		:title="title" 
		draggable="true" 
		:modal="false" 
		:width="width"
    :fullscreen="fullscreen"
    :show-close="false"
		>
    <template #header="{ close, titleId, titleClass }">
      <div class="my-header">
        <el-row :gutter="0">
          <el-col :xs="0" :sm="0" :md="8" :lg="4" :xl="4">
            <div v-html="svgIcon" style="display: inline; height: 10px; "></div>
          </el-col>
          <el-col :xs="4" :sm="4" :md="8" :lg="16" :xl="16">
            <div style="text-align: center">{{ title }}</div>
          </el-col>
          <el-col :xs="20" :sm="20" :md="8" :lg="4" :xl="4">
            <el-button text :icon="Minus" @click="() => {dialogVisible=false;fileApp=false;}" />
            <el-button text :icon="CopyDocument" @click="fullscreen=!fullscreen" />
            <el-button type="danger" text :icon="Close" @click="() => {dialogVisible=false;fileApp=false;}" />
          </el-col>
        </el-row>
      </div>
    </template>

		<div v-loading="loading">
			<iframe
        v-if="url !== ''"
				:src="url" 
				ref="myIframe" 
				frameborder="0" 
				:height="innerHeight"
				width="95%"
        :style="'height: ' + innerHeight + 'px'"
				@load="onIframeLoad" 
        @error="onIframeError"/>
      <Files v-else></Files>
<!--      <component v-else :is="component"></component>-->
		</div>

<!--    <component v-else :is="Files" @load="loading=false"></component>-->
	</el-dialog>

  <div class="desk">
	        <div class="apps">
                <!-- 桌面应用展示 -->
                  <div text plain :span="4" v-for="(item, i) in apps" class="app" :key="i" @click="open(item)">
                    <div v-html="item.img"></div>
                    <div class="name">{{ item.name }}</div>
                  </div>
	        </div>
  </div>

</template>

<script setup>
import {
  Minus,
    Close,
  CopyDocument
} from '@element-plus/icons-vue'
import { ref, reactive, computed, watch, defineAsyncComponent } from 'vue'
import Files from '@/view/Files.vue'
// const Files = ()=> import('@/view/Files.vue')
const dialogVisible = ref(false)
const myIframe = ref(null)
const loading = ref(true) // 用于记录 iframe 是否已加载完成
const url = ref('')
// const component = ref('')
const title = ref('')
const width = ref('')
const fullscreen = ref(false) // 全屏
const svgIcon = ref('') // 图标
const fileApp = ref(false)
const component = ref('Files')

// 计算浏览器高度
const innerHeight = ref(300)
watch(fullscreen, () => {
  if (fullscreen) {
    // 如果是全屏显示
    innerHeight.value = window.innerHeight - 60
  } else {
    innerHeight.value = 300
  }
})
const onIframeLoad = ()=> {
      loading.value = false // iframe 加载完成，将状态设置为 true
}

watch(url, () => {	// 监听url变量
	loading.value = true
  if (url.value === '') {
    loading.value = false
  }
})

